import { useEffect, useState, useContext } from "react";
import API from "../api/axios";
import { AuthContext } from "../context/AuthContext";

interface MilestoneDTO {
    id: string;
    title: string;
    description: string;
    dueDate: string;
    isCompleted: boolean;
    projectId: string;
    projectTitle: string;
    createdByName: string | null;
}

const MilestonesPage = () => {
    const { token, user } = useContext(AuthContext);
    const [milestones, setMilestones] = useState<MilestoneDTO[]>([]);
    const [projects, setProjects] = useState<{ id: string; title: string }[]>([]);
    const [selectedProject, setSelectedProject] = useState<string>("");
    const [newMilestone, setNewMilestone] = useState({ title: "", dueDate: "" });

    // Fetch projects for dropdown
    const fetchProjects = async () => {
        try {
            const res = await API.get("/projects", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setProjects(res.data);
            if (res.data.length) setSelectedProject(res.data[0].id);
        } catch (err) {
            console.error(err);
            alert("Failed to load projects");
        }
    };

    // Fetch milestones for selected project
    const fetchMilestones = async (projectId: string) => {
        if (!projectId) return;
        try {
            const res = await API.get(`/projects/${projectId}/milestones`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setMilestones(res.data);
        } catch (err) {
            console.error(err);
            alert("Failed to load milestones");
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    useEffect(() => {
        fetchMilestones(selectedProject);
    }, [selectedProject]);

    const handleAdd = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!selectedProject) return alert("Select a project");

        try {
            await API.post(
                `/projects/${selectedProject}/milestones`,
                newMilestone,
                {
                    headers: { Authorization: `Bearer ${token}` },
                    params: { username: user.sub },
                }
            );
            setNewMilestone({ title: "", dueDate: "" });
            fetchMilestones(selectedProject);
        } catch (err) {
            console.error(err);
            alert("Failed to add milestone");
        }
    };

    const handleDelete = async (id: string) => {
        if (!window.confirm("Delete this milestone?")) return;
        try {
            await API.delete(`/milestones/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchMilestones(selectedProject);
        } catch (err) {
            console.error(err);
            alert("Failed to delete milestone");
        }
    };

    return (
        <div className="container mt-5">
            <h2>Milestones</h2>

            {/* Project Dropdown */}
            <div className="mb-3">
                <label>Select Project:</label>
                <select
                    className="form-select"
                    value={selectedProject}
                    onChange={(e) => setSelectedProject(e.target.value)}
                >
                    {projects.map((p) => (
                        <option key={p.id} value={p.id}>{p.title}</option>
                    ))}
                </select>
            </div>

            {/* Add Milestone Form */}
            <form onSubmit={handleAdd} className="mb-4 row g-2">
                <div className="col-md-6">
                    <input
                        type="text"
                        placeholder="Title"
                        className="form-control"
                        value={newMilestone.title}
                        onChange={(e) =>
                            setNewMilestone({ ...newMilestone, title: e.target.value })
                        }
                        required
                    />
                </div>
                <div className="col-md-4">
                    <input
                        type="date"
                        className="form-control"
                        value={newMilestone.dueDate}
                        onChange={(e) =>
                            setNewMilestone({ ...newMilestone, dueDate: e.target.value })
                        }
                        required
                    />
                </div>
                <div className="col-md-2">
                    <button type="submit" className="btn btn-primary w-100">
                        Add
                    </button>
                </div>
            </form>

            {/* Milestone List */}
            <table className="table table-striped">
                <thead>
                <tr>
                    <th>Project</th>
                    <th>Title</th>
                    <th>Due Date</th>
                    <th>Completed</th>
                    <th>Created By</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {milestones.map((m) => (
                    <tr key={m.id}>
                        <td>{m.projectTitle}</td>
                        <td>{m.title}</td>
                        <td>{m.dueDate}</td>
                        <td>{m.isCompleted ? "✅" : "❌"}</td>
                        <td>{m.createdByName || "-"}</td>
                        <td>
                            <button
                                className="btn btn-sm btn-danger"
                                onClick={() => handleDelete(m.id)}
                            >
                                Delete
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default MilestonesPage;
