import { useEffect, useState, useContext, ChangeEvent } from "react";
import API from "../api/axios";
import { AuthContext } from "../context/AuthContext";

interface DocumentDTO {
    id: string;
    title: string;
    url: string;
    projectId: string;
    projectTitle: string;
    uploadedByName: string | null;
    uploadedAt: string;
}

interface Project {
    id: string;
    title: string;
}

const DocumentsPage = () => {
    const { token, user } = useContext(AuthContext);
    const [projects, setProjects] = useState<Project[]>([]);
    const [selectedProject, setSelectedProject] = useState<string>("");
    const [documents, setDocuments] = useState<DocumentDTO[]>([]);
    const [newDoc, setNewDoc] = useState({ title: "", file: null as File | null });

    // Fetch projects for dropdown
    const fetchProjects = async () => {
        try {
            const res = await API.get("/projects", {
                 headers: { Authorization: `Bearer ${token}` },
            });
            setProjects(res.data);
            if (res.data.length) setSelectedProject(res.data[0].id);
        } catch (err) {
            console.error(err);
            alert("Failed to load projects");
        }
    };

    // Fetch documents for selected project
    const fetchDocuments = async (projectId: string) => {
        if (!projectId) return;
        try {
            const res = await API.get(`/projects/${projectId}/documents`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setDocuments(res.data);
        } catch (err) {
            console.error(err);
            alert("Failed to load documents");
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    useEffect(() => {
        fetchDocuments(selectedProject);
    }, [selectedProject]);

    const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
        if (e.target.files && e.target.files[0]) {
            setNewDoc({ ...newDoc, file: e.target.files[0] });
        }
    };

    const handleUpload = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!newDoc.file || !selectedProject) {
            alert("Select a file and project");
            return;
        }

        const formData = new FormData();
        formData.append("title", newDoc.title);
        formData.append("file", newDoc.file);

        try {
            await API.post(
                `/projects/${selectedProject}/documents`,
                formData,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "multipart/form-data",
                    },
                    params: { userId: user.sub }, // assuming backend needs userId
                }
            );
            setNewDoc({ title: "", file: null });
            fetchDocuments(selectedProject);
        } catch (err) {
            console.error(err);
            alert("File upload failed");
        }
    };

    const handleDelete = async (id: string) => {
        if (!window.confirm("Delete this document?")) return;
        try {
            await API.delete(`/documents/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchDocuments(selectedProject);
        } catch (err) {
            console.error(err);
            alert("Failed to delete document");
        }
    };

    return (
        <div className="container mt-5">
            <h2>Documents</h2>

            {/* Project Dropdown */}
            <div className="mb-3">
                <label>Select Project:</label>
                <select
                    className="form-select"
                    value={selectedProject}
                    onChange={(e) => setSelectedProject(e.target.value)}
                >
                    {projects.map((p) => (
                        <option key={p.id} value={p.id}>
                            {p.title}
                        </option>
                    ))}
                </select>
            </div>

            {/* Upload Form */}
            <form onSubmit={handleUpload} className="mb-4 row g-2">
                <div className="col-md-5">
                    <input
                        type="text"
                        placeholder="Title"
                        className="form-control"
                        value={newDoc.title}
                        onChange={(e) => setNewDoc({ ...newDoc, title: e.target.value })}
                        required
                    />
                </div>
                <div className="col-md-5">
                    <input type="file" className="form-control" onChange={handleFileChange} required />
                </div>
                <div className="col-md-2">
                    <button type="submit" className="btn btn-primary w-100">
                        Upload
                    </button>
                </div>
            </form>

            {/* Document List */}
            <table className="table table-striped">
                <thead>
                <tr>
                    <th>Project</th>
                    <th>Title</th>
                    <th>Uploaded By</th>
                    <th>Uploaded At</th>
                    <th>File</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {documents.map((d) => (
                    <tr key={d.id}>
                        <td>{d.projectTitle}</td>
                        <td>{d.title}</td>
                        <td>{d.uploadedByName}</td>
                        <td>{d.uploadedAt}</td>
                        <td>
                            <a href={d.url} target="_blank" rel="noopener noreferrer">
                                View
                            </a>
                        </td>
                        <td>
                            <button className="btn btn-sm btn-danger" onClick={() => handleDelete(d.id)}>
                                Delete
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default DocumentsPage;
