import { NavLink } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
    const { user, logout } = useContext(AuthContext);

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <NavLink className="navbar-brand" to="/">
                    Research Tracker
                </NavLink>

                <div className="d-flex align-items-center">
                    {/* Links visible only if logged in */}
                    {user && (
                        <>
                            <NavLink className="nav-link" to="/projects">Projects</NavLink>
                            <NavLink className="nav-link" to="/milestones">Milestones</NavLink>
                            <NavLink className="nav-link" to="/documents">Documents</NavLink>
                            {user.role === "ADMIN" && (
                                <NavLink className="nav-link" to="/admin">Admin</NavLink>
                            )}
                            <button className="btn btn-outline-danger ms-2" onClick={logout}>
                                Logout
                            </button>
                        </>
                    )}

                    {/* Links visible if not logged in */}
                    {!user && (
                        <>
                            <NavLink className="nav-link" to="/login">Login</NavLink>
                            <NavLink className="nav-link" to="/register">Register</NavLink>
                        </>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
